import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-sucessfull-tap',
  templateUrl: './sucessfull-tap.component.html',
  styleUrls: ['./sucessfull-tap.component.css']
})
export class SucessfullTapComponent {
@Input() name: string = '';
@Input() rollno: string = '';
@Input() image: string = '';
}
<div style="background-color: transparent;width: 470px;height: 650px;margin: 50px auto;border-radius: 20px;display: flex;flex-direction: column;align-items: center;justify-content: flex-start;box-shadow: 0 0 20px rgb(193, 206, 234);padding: 10px;">

    <div style="background: linear-gradient(to bottom, #1e3a8a 0%, transparent 100%);height: 60%;width: 100%;border-radius: 10px;display: flex;flex-direction: column;align-items: center;justify-content: flex-start;overflow: hidden;">
      <img src="../../assets/userImages/{{image}}" style="width: 50%;height: 80%; margin-top: 40px;"/>
      <!-- <img src="../../assets/userImages/{{image}}" style="width: 50%;height: 80%; margin-top: 40px;"/> -->

    </div>

    <div style="color: white;font-family: inter, sans-serif;font-size: 35px;font-weight: 700;text-align: center;margin-top: 20px; ">{{name}}</div>
    <div style="color: #02b9f5;font-family: inter, sans-serif;font-size: 28px;font-weight: 700;text-align: center;margin-top: 10px; margin-bottom: 50px;">{{rollno}}</div>
    <div style="color: white;font-family: 'Allura', cursive;font-size: 70px;font-weight: 100;text-align: center;margin-top: 10px;">Welcome</div>

</div>
