import { Component, OnInit } from '@angular/core';
import { io, Socket } from 'socket.io-client';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  rollNo: string = '';
  name: string = '';
  photoUrl: string = '';
  page: string = 'tap';
  showPhoto: boolean = false;

  socket: Socket = io('http://localhost:5000');  

  ngOnInit() {
    this.socket.on('rfid_data', (data: any) => {
      this.rollNo = data.data;

      if (data.student && !data.student.error) {
        this.name = data.student.Name;
        this.photoUrl = data.student.Photo;
        this.showPhoto = true;
        console.log(this.photoUrl);
        this.page = "success";
        setTimeout(() => {this.page = "tap";}, 3000); 
      } else {
        this.name = 'Unknown';
        this.photoUrl = '../assets/dewang.png';
        this.showPhoto = true;
        this.page = "error";
        setTimeout(() => {this.page = "tap";}, 3000); 
      }
    });
  }
}
<div style="background-image: url('../assets/backdrop.png'); height: 100vh; display: flex; flex-direction: column; align-items: center; color: white; font-family: Arial, sans-serif;">

  <div style="display: flex; align-items: center; padding: 20px;"><img src="../assets/logo.png" alt="College Logo" style="margin-right: 20px;"></div>
  <h2 style="font-family: 'Geist', sans-serif;font-size: 80px;font-weight: 700;margin: 20px 0;background: linear-gradient(to right, #ffffff, #7982ff);-webkit-background-clip: text;-webkit-text-fill-color: transparent;">SAMYAGDARSHAN 2025</h2>
  <h3 style="font-size: 85px; color: #fff; margin: 0; font-family: 'Allura', cursive; margin-bottom: 80px;">College Annual Day</h3> 

  <div *ngIf="page == 'tap'"> <app-tap-card></app-tap-card> </div>
  <div *ngIf="page == 'sucess'"> <app-sucessfull-tap name={{name}} rollno={{rollNo}} image={{photoUrl}}></app-sucessfull-tap></div>
  <div *ngIf="page == 'error'"> <app-error-tap></app-error-tap> </div>

  <div style="margin-top: auto; display: flex; flex-direction: column; align-items: center; width: 100%; padding-bottom: 20px;"><app-footer></app-footer></div>

</div>
